package com.stmikakba.andryantoaman.appgithubimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.request.RequestOptionsimport com.stmikakba.andryantoaman.appgithub.UserDetailActivity.Companion.EXTRA_USERimport kotlinx.android.synthetic.main.item_list_main.view.*class ListUserAdapter(    private val listUser: ArrayList<User>) : RecyclerView.Adapter<ListUserAdapter.ListViewHolder>(){    inner class ListViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        fun bind(user: User) {            with(itemView){                Glide.with(itemView.context)                    .load(user.photo)                    .apply(RequestOptions().override(55, 55))                    .into(iv_item_photo)                tv_item_nama.text = user.name                tv_item_username.text = user.username            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ListViewHolder {        val view: View = LayoutInflater            .from(parent.context)            .inflate(R.layout.item_list_main, parent, false)        return ListViewHolder(view)    }    override fun onBindViewHolder(holder: ListViewHolder, position: Int) {        holder.bind(listUser[position])        holder.itemView.setOnClickListener {            val intent = Intent(holder.itemView.context,            UserDetailActivity::class.java                    )            intent.putExtra(EXTRA_USER,listUser)            holder.itemView.context.startActivity(intent)        }    }        override fun getItemCount(): Int = listUser.size}//            intent.putExtra("AVATAR",listUser[position].photo)//            intent.putExtra("NAMA",listUser[position].name)//            intent.putExtra("USERNAME",listUser[position].username)//            intent.putExtra("FOLLOWER"